package week2.day1;

public class FindEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Printing odd numbers
		for (int i=0; i<=10; i++) {
			if (i%2==1) {
				System.out.println(i);
			}
		}
		
		//Printing even numbers
		for (int i=0; i<=10; i++) {
			if (i%2==0) {
				System.out.println(i);
			}
		}

	}

}
